import React, { useState, useMemo } from "react";
import {
  View,
  Text,
  FlatList,
  TouchableOpacity,
  TextInput,
  Alert,
  StyleSheet,
  ScrollView,
} from "react-native";
import { Ionicons } from "@expo/vector-icons";
import { SafeAreaView } from "react-native-safe-area-context";
import { useFinance } from "../context/FinanceContext";
import { Transaction } from "../context/FinanceContext";
import Toast from "../components/Toast";
import { useToast } from "../hooks/useToast";

type FilterType = "all" | "income" | "expense";
type SortType = "date" | "amount-desc" | "amount-asc" | "description";

export default function ListScreen() {
  const { transactions, removeTransaction } = useFinance();
  const { toastState, showSuccess, showError, hideToast } = useToast();
  const [searchText, setSearchText] = useState("");
  const [filterType, setFilterType] = useState<FilterType>("all");
  const [selectedCategory, setSelectedCategory] = useState<string>("all");
  const [sortBy, setSortBy] = useState<SortType>("date");
  const [showFilters, setShowFilters] = useState(false);

  // Obter categorias únicas
  const uniqueCategories = useMemo(() => {
    const categories = [...new Set(transactions.map((t) => t.category))];
    return categories.sort();
  }, [transactions]);

  const filteredTransactions = useMemo(() => {
    let filtered = [...transactions];

    // Filtro por texto
    if (searchText) {
      filtered = filtered.filter(
        (transaction) =>
          transaction.description
            .toLowerCase()
            .includes(searchText.toLowerCase()) ||
          transaction.category.toLowerCase().includes(searchText.toLowerCase())
      );
    }

    // Filtro por tipo
    if (filterType !== "all") {
      filtered = filtered.filter(
        (transaction) => transaction.type === filterType
      );
    }

    // Filtro por categoria
    if (selectedCategory !== "all") {
      filtered = filtered.filter(
        (transaction) => transaction.category === selectedCategory
      );
    }

    // Ordenação
    filtered.sort((a, b) => {
      switch (sortBy) {
        case "date":
          return new Date(b.date).getTime() - new Date(a.date).getTime();
        case "amount-desc":
          return b.amount - a.amount; // Maior para menor
        case "amount-asc":
          return a.amount - b.amount; // Menor para maior
        case "description":
          return a.description.localeCompare(b.description);
        default:
          return 0;
      }
    });

    return filtered;
  }, [transactions, searchText, filterType, selectedCategory, sortBy]);
  const formatCurrency = (value: number): string => {
    return new Intl.NumberFormat("pt-BR", {
      style: "currency",
      currency: "BRL",
    }).format(value);
  };

  const formatDate = (dateString: string): string => {
    const date = new Date(dateString);
    return date.toLocaleDateString("pt-BR", {
      day: "2-digit",
      month: "2-digit",
      year: "numeric",
    });
  };

  const handleRemoveTransaction = (id: string, description: string) => {
    console.log("handleRemoveTransaction chamada para:", id, description);
    Alert.alert(
      "Confirmar Exclusão",
      `Deseja realmente excluir a transação "${description}"?`,
      [
        {
          text: "Cancelar",
          style: "cancel",
        },
        {
          text: "Excluir",
          style: "destructive",
          onPress: () => {
            try {
              console.log("Removendo transação:", id, description);
              removeTransaction(id);
              showSuccess(`Transação removida com sucesso!`);
            } catch (error) {
              console.error("Erro ao remover transação:", error);
              showError("Erro ao remover transação. Tente novamente.");
            }
          },
        },
      ]
    );
  };
  const renderTransaction = ({ item }: { item: Transaction }) => (
    <View style={styles.transactionItem}>
      <View
        style={[
          styles.iconContainer,
          {
            backgroundColor: item.type === "income" ? "#28A745" : "#DC3545",
          },
        ]}
      >
        <Ionicons
          name={item.type === "income" ? "trending-up" : "trending-down"}
          size={20}
          color="#fff"
        />
      </View>

      {/* Conteúdo da transação */}
      <View style={styles.transactionContent}>
        <View style={styles.transactionInfo}>
          <Text style={styles.transactionDescription} numberOfLines={1}>
            {item.description}
          </Text>
          <Text style={styles.transactionCategory}>{item.category}</Text>
          <Text style={styles.transactionDate}>{formatDate(item.date)}</Text>
        </View>

        <View style={styles.transactionAmount}>
          <Text
            style={[
              styles.amountText,
              {
                color: item.type === "income" ? "#28A745" : "#DC3545",
              },
            ]}
          >
            {item.type === "expense" && "-"}
            {formatCurrency(item.amount)}
          </Text>
        </View>
      </View>

      {/* Botão de remoção */}
      <TouchableOpacity
        style={styles.removeButton}
        onPress={() => {
          console.log("Botão de remoção pressionado");
          handleRemoveTransaction(item.id, item.description);
        }}
        activeOpacity={0.7}
        hitSlop={{ top: 10, bottom: 10, left: 10, right: 10 }}
      >
        <Ionicons name="trash-outline" size={22} color="#DC3545" />
      </TouchableOpacity>
    </View>
  );

  // Estado vazio
  const renderEmptyState = () => (
    <View style={styles.emptyState}>
      <Ionicons name="receipt-outline" size={80} color="#ccc" />
      <Text style={styles.emptyStateTitle}>Nenhuma transação encontrada</Text>
      <Text style={styles.emptyStateSubtitle}>
        {searchText
          ? "Tente usar outros termos de busca"
          : "Adicione sua primeira transação usando a aba 'Adicionar'"}
      </Text>
    </View>
  );

  return (
    <SafeAreaView style={styles.container}>
      {/* Header com busca */}
      <View style={styles.header}>
        <View style={styles.searchContainer}>
          <Ionicons name="search-outline" size={20} color="#666" />
          <TextInput
            style={styles.searchInput}
            placeholder="Buscar transações..."
            value={searchText}
            onChangeText={setSearchText}
            placeholderTextColor="#999"
          />
          {searchText ? (
            <TouchableOpacity onPress={() => setSearchText("")}>
              <Ionicons name="close-circle" size={20} color="#666" />
            </TouchableOpacity>
          ) : null}
        </View>

        {/* Botão de filtros e estatísticas */}
        <View style={styles.headerBottom}>
          <Text style={styles.statsText}>
            {filteredTransactions.length} transação(ões)
          </Text>
          <TouchableOpacity
            style={styles.filterButton}
            onPress={() => setShowFilters(!showFilters)}
          >
            <Ionicons
              name={showFilters ? "funnel" : "funnel-outline"}
              size={20}
              color="#007AFF"
            />
            <Text style={styles.filterButtonText}>Filtros</Text>
          </TouchableOpacity>
        </View>
      </View>

      {/* Seção de Filtros */}
      {showFilters && (
        <View style={styles.filtersContainer}>
          {/* Filtros por Tipo */}
          <View style={styles.filterSection}>
            <Text style={styles.filterTitle}>Tipo</Text>
            <ScrollView horizontal showsHorizontalScrollIndicator={false}>
              <View style={styles.filterChips}>
                {(["all", "income", "expense"] as FilterType[]).map((type) => (
                  <TouchableOpacity
                    key={type}
                    style={[
                      styles.chip,
                      filterType === type && styles.chipSelected,
                    ]}
                    onPress={() => setFilterType(type)}
                  >
                    <Text
                      style={[
                        styles.chipText,
                        filterType === type && styles.chipTextSelected,
                      ]}
                    >
                      {type === "all"
                        ? "Todos"
                        : type === "income"
                        ? "Receitas"
                        : "Despesas"}
                    </Text>
                  </TouchableOpacity>
                ))}
              </View>
            </ScrollView>
          </View>

          {/* Filtros por Categoria */}
          <View style={styles.filterSection}>
            <Text style={styles.filterTitle}>Categoria</Text>
            <ScrollView horizontal showsHorizontalScrollIndicator={false}>
              <View style={styles.filterChips}>
                <TouchableOpacity
                  style={[
                    styles.chip,
                    selectedCategory === "all" && styles.chipSelected,
                  ]}
                  onPress={() => setSelectedCategory("all")}
                >
                  <Text
                    style={[
                      styles.chipText,
                      selectedCategory === "all" && styles.chipTextSelected,
                    ]}
                  >
                    Todas
                  </Text>
                </TouchableOpacity>
                {uniqueCategories.map((category) => (
                  <TouchableOpacity
                    key={category}
                    style={[
                      styles.chip,
                      selectedCategory === category && styles.chipSelected,
                    ]}
                    onPress={() => setSelectedCategory(category)}
                  >
                    <Text
                      style={[
                        styles.chipText,
                        selectedCategory === category &&
                          styles.chipTextSelected,
                      ]}
                    >
                      {category}
                    </Text>
                  </TouchableOpacity>
                ))}
              </View>
            </ScrollView>
          </View>

          {/* Ordenação */}
          <View style={styles.filterSection}>
            <Text style={styles.filterTitle}>Ordenar por</Text>
            <ScrollView horizontal showsHorizontalScrollIndicator={false}>
              <View style={styles.filterChips}>
                {(
                  [
                    { key: "date", label: "Data" },
                    { key: "amount-desc", label: "Maior Valor" },
                    { key: "amount-asc", label: "Menor Valor" },
                    { key: "description", label: "Descrição" },
                  ] as { key: SortType; label: string }[]
                ).map((sort) => (
                  <TouchableOpacity
                    key={sort.key}
                    style={[
                      styles.chip,
                      sortBy === sort.key && styles.chipSelected,
                    ]}
                    onPress={() => setSortBy(sort.key)}
                  >
                    <Text
                      style={[
                        styles.chipText,
                        sortBy === sort.key && styles.chipTextSelected,
                      ]}
                    >
                      {sort.label}
                    </Text>
                  </TouchableOpacity>
                ))}
              </View>
            </ScrollView>
          </View>
        </View>
      )}

      {/* Lista de transações */}
      <FlatList
        data={filteredTransactions}
        keyExtractor={(item) => item.id}
        renderItem={renderTransaction}
        ListEmptyComponent={renderEmptyState}
        showsVerticalScrollIndicator={false}
        contentContainerStyle={
          filteredTransactions.length === 0 ? styles.emptyContainer : undefined
        }
        style={styles.list}
      />

      {/* Toast de notificação */}
      <Toast
        visible={toastState.visible}
        message={toastState.message}
        type={toastState.type}
        onHide={hideToast}
      />
    </SafeAreaView>
  );
}

